log.info "No Key"
myTestStep.assertions["XPath Match - <ScoreFactors><ScoreFactor1> Check if tag exists in STG Response"].disabled = true myTestStep.assertions["XPath Match - <ScoreFactors><ScoreFactor2> Check if tag exists in STG Response"].disabled = true myTestStep.assertions["XPath Match - <ScoreFactors><ScoreFactor3> Check if tag exists in STG Response"].disabled = true myTestStep.assertions["XPath Match - <ScoreFactors><ScoreFactor4> Check if tag exists in STG Response"].disabled = true myTestStep.assertions["XPath Match - <Attributes><Attributes01Day> Check if tag does not exists in STG Response"].disabled = true myTestStep.assertions["XPath Match - <Attributes><Attributes03Day> Check if tag does not exists in STG Response"].disabled = true myTestStep.assertions["XPath Match - <Attributes><Attributes07Day> Check if tag does not exists in STG Response"].disabled = true myTestStep.assertions["XPath Match - <Attributes><Attributes21Day> Check if tag does not exists in STG Response"].disabled = true myTestStep.assertions["XPath Match - <Attributes><Attributes28Day> Check if tag does not exists in STG Response"].disabled = true myTestStep.assertions["XPath Match - <Attributes><Attributes90Day> Check if tag does not exists in STG Response"].disabled = true }else{
