Select *
From fcr001.cm_rqst_log_${#Project#CurYear}_${#Project#CurMonth}
Where client_trn_id = :ReviewReferenceID

Select *
From fcr001.cm_resp_log_${#Project#CurYear}_${#Project#CurMonth}
