def myTestStep = context.testCase.getTestStepByName("REST Request - STG First Inquiry")
if ( NetConnectResponse.Products.CustomerManagement.ScoreResults.Score.text() == '999' || NetConnectResponse.Products.CustomerManagement.ScoreResults.Score.text() == '9999' || NetConnectResponse.Products.CustomerManagement.ScoreResults.Score.text() == '9001' || NetConnectResponse.Products.CustomerManagement.ScoreResults.Score.text() == '9012'){
log.info "No Key"
myTestStep.assertions["XPath Match - <ScoreFactors><ScoreFactor1> Check if tag exists in STG Response"].disabled = true myTestStep.assertions["XPath Match - <ScoreFactors><ScoreFactor2> Check if tag exists in STG Response"].disabled = true myTestStep.assertions["XPath Match - <ScoreFactors><ScoreFactor3> Check if tag exists in STG Response"].disabled = true myTestStep.assertions["XPath Match - <ScoreFactors><ScoreFactor4> Check if tag exists in STG Response"].disabled = true myTestStep.assertions["XPath Match - <Attributes><Attributes01Day> Check if tag does not exists in STG Response"].disabled = true myTestStep.assertions["XPath Match - <Attributes><Attributes03Day> Check if tag does not exists in STG Response"].disabled = true myTestStep.assertions["XPath Match - <Attributes><Attributes07Day> Check if tag does not exists in STG Response"].disabled = true myTestStep.assertions["XPath Match - <Attributes><Attributes21Day> Check if tag does not exists in STG Response"].disabled = true myTestStep.assertions["XPath Match - <Attributes><Attributes28Day> Check if tag does not exists in STG Response"].disabled = true myTestStep.assertions["XPath Match - <Attributes><Attributes90Day> Check if tag does not exists in STG Response"].disabled = true }else{
log.info "Has Key"
myTestStep.assertions["XPath Match - <ScoreFactors><ScoreFactor1> Check if tag exists in STG Response"].disabled = false myTestStep.assertions["XPath Match - <ScoreFactors><ScoreFactor2> Check if tag exists in STG Response"].disabled = false myTestStep.assertions["XPath Match - <ScoreFactors><ScoreFactor3> Check if tag exists in STG Response"].disabled = false myTestStep.assertions["XPath Match - <ScoreFactors><ScoreFactor4> Check if tag exists in STG Response"].disabled = false myTestStep.assertions["XPath Match - <Attributes><Attributes01Day> Check if tag does not exists in STG Response"].disabled = false myTestStep.assertions["XPath Match - <Attributes><Attributes03Day> Check if tag does not exists in STG Response"].disabled = false myTestStep.assertions["XPath Match - <Attributes><Attributes07Day> Check if tag does not exists in STG Response"].disabled = false myTestStep.assertions["XPath Match - <Attributes><Attributes21Day> Check if tag does not exists in STG Response"].disabled = false myTestStep.assertions["XPath Match - <Attributes><Attributes28Day> Check if tag does not exists in STG Response"].disabled = false myTestStep.assertions["XPath Match - <Attributes><Attributes90Day> Check if tag does not exists in STG Response"].disabled = false
